  },
  {
    "line": 65,
    "content": "    @staticmethod"
  },
  {
    "line": 66,
    "content": "    def euclidean_distance(pos1: Tuple[int, int], pos2: Tuple[int, int]) -> float:"
  },
  {
    "line": 67,
    "content": "        \"\"\""
  },
  {
    "line": 68,
    "content": "        计算欧几里得距离（L2距离）"
  },
  {
    "line": 69,
    "content": "        "
  },
  {
    "line": 70,
    "content": "        参数:"
  },
  {
    "line": 71,
    "content": "            pos1: 位置1，格式为(x, y)"
  },
  {
    "line": 72,
    "content": "            pos2: 位置2，格式为(x, y)"
  },
  {
    "line": 73,
    "content": "        "
  },
  {
    "line": 74,
    "content": "        返回:"
  },
  {
    "line": 75,
    "content": "            欧几里得距离"
  },
  {
    "line": 76,
    "content": "        \"\"\""
  },
  {
    "line": 77,
